mapper.select:
select review_no, order_no, product_no, user_id, review_content, review_regdate, delete_state, review_deldate
from reviews
where review_no = #{review_no}

		select rnum, review_no, order_no, product_no, user_id, review_content, review_regdate, delete_state, review_deldate
    from (
        select rownum as rnum, order_no, product_no, user_id, review_content, review_regdate, delete_state, review_deldate
        from (
            select review_no, order_no, product_no, user_id, review_content, review_regdate, delete_state, review_deldate
            from reviews
            order by review_regdate desc
        )
        where rownum <= #{endRowNo}
    )
    where rnum >= #{startRowNo}
